days_left_to_christmas = int(input())
ornament_set, tree_skirt, tree_garland, tree_lights = 2, 5, 3, 15
total_cost = 0
spirit = 0

for days in range(1, days_left_to_christmas + 1):
    if days % 11 == 0:
        quantity += 2
    if days % 2 == 0:
        total_cost += ornament_set * quantity
        spirit += 5
    if days % 3 == 0:
        total_cost += (tree_skirt * quantity) + (tree_garland * quantity)
        spirit += 13
    if days % 5 == 0:
        total_cost += tree_lights * quantity
        spirit += 17
        if days % 3 == 0:
            spirit += 30
    if days % 10 == 0:
        spirit -= 20
        total_cost += tree_lights + tree_garland + tree_skirt

if days_left_to_christmas % 10 == 0:
    spirit -= 30

print(f"Total cost: {total_cost}")
print(f"Total spirit: {spirit}")

